let myLeads=[];const inputEl=document.getElementById("input-el"),inputBtn=document.getElementById("input-btn"),deleteBtn=document.getElementById("delete-btn"),ulEl=document.getElementById("ul-el");let leadsFromLocalStorage=JSON.parse(localStorage.getItem("myLeads"));function renderLeads(){let e="";for(let t=0;t<myLeads.length;t++)e+=`\n            <li>\n                <a target='_blank' href='${myLeads[t]}'>\n                    ${myLeads[t]}\n                </a>\n            </li>\n        `;ulEl.innerHTML=e}leadsFromLocalStorage&&(myLeads=leadsFromLocalStorage,renderLeads()),inputBtn.addEventListener("click",(function(){myLeads.push(inputEl.value),inputEl.value="",localStorage.setItem("myLeads",JSON.stringify(myLeads)),renderLeads()}));